$(function(){
    tinymce.PluginManager.add("alto_snippets", function (editor, e) {
        editor.addButton('snippets',
            {
                text: 'a:',
                classes: 'snippets widget btn btn-small',
                type: 'menubutton',
                image: tinymce.baseURL + '/plugins/snippets/img/editor_snippet.png',
                menu: [{
                    text: ls.lang.get('panel_photoset'),
                    onclick: function () {
                        editor.execCommand('InsertPhotoset', false, '');
                    }
                },
                    {
                        text: ls.lang.get('panel_user'),
                        onclick: function () {
                            editor.execCommand('InsertUsername', false, '');
                        }
                    },
                    {
                        text: ls.lang.get('panel_spoiler'),
                        onclick: function () {
                            editor.execCommand('InsertSpoiler', false, '');
                        }
                    }
                ]
            });


    });
});




